<script>import MarkdownToken from "../../MarkdownToken.svelte";
export let token;
export let options;
export let renderers;
let component;
$: component = token.ordered ? "ol" : "ul";
</script>

<svelte:element this={component} start={token.start || 1}>
  {#each token.items as item}
